function plotLineLow(x0, y0, x1, y1, drawFunc) {
  var dx = x1 - x0;
  var dy = y1 - y0;
  var yi = 1;
  if (dy < 0) {
    yi = -1;
    dy = -dy;
  }
  var D = 2 * dy - dx;
  var y = y0;
  for (var x = x0; x < x1 + 1; x++) {
    drawFunc(x, y);
    if (D > 0) {
      y = y + yi;
      D = D + 2 * (dy - dx);
    } else {
      D = D + 2 * dy;
    }
  }
}
