import './App.css';
import React, { createRef } from 'react';


const UPDATEPERIOD = 1000;

const CHUNKSIZE = 16;

const LOWESTSCALE = 3;

const STARTSCALE = 5;

const DRAWTOOL = 0;
const EYEDROPTOOL = 1;
const MOVETOOL = 2;

// const blocks = [
//     {
//         blockid: '0',
//         blockname: '1',
//         imagesrc: '#ffffff'
