import './App.css';
import {plotLine, Vector2D} from '../../common/common.js';
import React, { createRef } from 'react';


const blocks = [
    {
        blockid: 0,
        blockname: '1',
        imagesrc: '#ffffff'
    },
    {
        blockid: 1,
        blockname: '2',
        imagesrc: '#d1e7dc'
    },
    {
        blockid: 2,
        blockname: '3',
        imagesrc: '#c4d4e1'
    },
    {
        blockid: 3,
        blockname: '4',
        imagesrc: '#bcb5d3'
    },
    {
        blockid: 4,
        blockname: '5',
        imagesrc: '#f5dbe2'
    },
    {
        blockid: 5,
        blockname: '6',
        imagesrc: '#eeb6c9'
    },
    {
        blockid: 6,
        blockname: '7',
        imagesrc: '#c49abf'
    },
    {
        blockid: 7,
        blockname: '8',
        imagesrc: '#bada55'
    },
    {
        blockid: 8,
        blockname: '9',
        imagesrc: '#ffd700'
    },
    {
        blockid: 9,
        blockname: '10',
        imagesrc: '#f47b79'
    }
];


class Map {
    constructor(w, h) {

        this.canvasdimensions = new Vector2D(w,h); // establishing at start means no resizing canvas element
        this.matrix = null;

        // this.cachedChunks = [];

        this.lastchunkrequesttime = null;

    }
    makeMatrix(columns, rows, startx, starty, chunksize) {
        let grid = Array(columns);
        for (let i=0;i<columns;i++) {
            grid[i] = Array(rows);
            for (let j=0;j<rows;j++) {

                // this.cachedChunks.forEach(element => {
                //     if (element.x===i+startx && element.y===j+starty) {
                //         grid[i][j] = element;
                //     }
                // });
                let data = { "x": i, "y": j };
